import React, {useState, useEffect, Component } from 'react';
import {StyleSheet, FlatList, LisHeaderComponent, TouchableHighlight, ListFooterComponent, Image, ImageBackground, TouchableWithoutFeedback, TextInput, ScrollView, SafeAreaView, View, Text, Alert, Pressable, Platform, StatusBar} from 'react-native';

export default function SuccessPageScreen({navigation, route}){
  useEffect(()=>{
  }, [])

  const addRecord =()=> {

    this.isDisabled = true
    this.opacity = this.opacity_enable
    this.submit='Please wait..'
    const form = new FormData();
    form.append('surname', this.surname)
    form.append('firstname', this.firstname)
    form.append('email', this.email_one)
    form.append('phone', this.phone_one)
    form.append('countryCode', this.countryCode)
    form.append('personalId', this.personalId)
    form.append('csrfmiddlewaretoken', this.token)
    axios.post('/posts/admin/new/', form)
    .then(response => {
    if(response.data.status==response.data.statusmsg){
    this.classname=response.data.classname
    this.alert=response.data.msg
    this.submit=this.submittxt

    this.isDisabled = false
    this.opacity = this.opacity_disable
    this.formSuccess = 1
    this.surname = ''
    this.firstname = ''
    this.countryCode = ''
    this.phone_one = ''
    this.email_one = ''
    this.personalId = ''
    }else{
    this.classname=response.data.classname
    this.alert=response.data.msg
    this.submit=this.submittxt

    this.isDisabled = false
    this.opacity = this.opacity_disable
    this.formSuccess = 2

    }
  }).catch(()=>{
    this.isDisabled = false
    this.opacity = this.opacity_disable
    this.formSuccess = 2
  })  
  }


    return (
              <View style={styles.container}>
                <View style={styles.div1}>
              <Image source={require('../assets/img/logo4.png')} style={{ width: '95%', height:150, alignSelf:'center'}} resizeMode={'center'}></Image>
              </View>
                <View style={styles.div3}>
                             <Text style={styles.textMiddleHeader}>Thanks, {navigation.getParam('surname')}!</Text>
                             <Text style={styles.textMiddle}>We have received your information provided!</Text>
                </View>

                <View style={styles.div4}>
        <TouchableHighlight style={styles.pressableBtn} onPress={()=> navigation.navigate('Home')}>
          <Text style={styles.pressableBtnTxt}>Close this page</Text>
        </TouchableHighlight>
      </View>
      </View>

    )
}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'column',
    backgroundColor: '#fdfbc9',
    paddingTop: Platform.OS === "android" ? StatusBar.currentHeight : 0
  },


  wrapper:{
    flex: 1,
    flexDirection: 'column',
    alignItems: 'stretch',
    padding:10,
  },
  textMiddleHeader: {
    textAlign: 'center',
    fontSize: 30,
    marginBottom: 10,
    fontWeight: 'bold',
    color: '#008852',
    textTransform: 'capitalize'

  },
  textMiddle: {
    textAlign: 'center',
    fontSize: 25,
    color: '#008852',

  },

  div1:{
    flex: 2,
    flexDirection: "column",
    alignItems: 'center',
  },

  div3:{
    flex: 3,
    flexDirection: "column",
  },
  div4:{
    flex: 1,
    flexDirection: "column",
    justifyContent: 'center'

  },

  pressableBtn:{
    width: '90%',
    padding: 10,
    paddingTop: 13,
    paddingBottom: 13,
    backgroundColor: '#008852',
    alignSelf:'center',
    elevation: 3,
    borderRadius: 5,
    shadowOpacity: 0.5,
  },
  pressableBtnTxt:{
    color: '#fff',
    textAlign: 'center',
    fontSize: 20,
    textTransform: 'none'
}

});
