import React, {Component } from 'react';
import {StyleSheet, ImageBackground, TextInput, SafeAreaView, View, Text, Image, TouchableOpacity, Pressable, Platform, StatusBar} from 'react-native';

export default function Homescreen({ navigation }){
    state = {
      surname: '',
      firstname: '',
      email: '',
      phone: '',
    }

    getSurname = (text) => {
      this.setState =({email: text})
    }
    getFirstname = (text) => {
      this.setState =({firstname: text})
    }
    getFirstname = (text) => {
      this.setState =({firstname: text})
    }

    return (
              <SafeAreaView style={styles.container}>
        <ImageBackground source={require('../img/bg.jpg')} resizeMode="repeat" style={styles.image}>
              <View style={styles.wrapper}>
      <View style={styles.div1}>
        <Text style={styles.companyName}>VirtualStack</Text>
      </View>
      <View style={styles.div3}>
        <View style={styles.wrapInput}>
          <Text style={styles.formLabel}>Surname</Text>
            <TextInput placeholder='Surname'
             style={styles.controlField}
             autoCapitalize= "words"
             onChangeText={this.getSurname}
             />

    </View>
        <Pressable style={styles.pressableBtn} onPress={()=> navigation.navigate('Registration')}>
          <Text style={styles.pressableBtnTxt}>Register for the event</Text>
        </Pressable>
      </View>
      </View>
      </ImageBackground>
      </SafeAreaView>

    )
}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    paddingTop: Platform.OS === "android" ? StatusBar.currentHeight : 0
  },

  companyName:{
    fontSize: 24,
  },
  wrapper:{
    flex: 1,
    padding:10,
    overflow: 'scroll'

  },
  div1:{
    flex: 0.5,
    flexDirection: "column",

  },

  div3:{
    flex: 5,
    marginTop: 30,
    flexDirection: "column",
    justifyContent: 'space-evenly',

  },
  image:{
    flex: 1, 
  },
  wrapInput:{
    margin: 15,
  },
  controlField:{
    height: 40,
    borderColor: '#ccc',
    borderWidth: 1,
    padding: 7,
    borderRadius: 4,
    fontSize: 22,

  },
  sloganTxt:{
    textAlign: 'center',
    fontSize: 24,
    marginBottom: 10,
    marginTop: 10,
  },
  imgContainer: {
    marginTop: 20,
  },
  BtnText:{
    color:'#fff',
    textAlign:'center',
    fontSize:18,
  },

  pressableBtn:{
    width: '80%',
    padding: 10,
    paddingTop: 13,
    paddingBottom: 13,
    backgroundColor:'#2196F3',
    alignSelf:'center',
    elevation: 3,
    borderRadius: 5,
    shadowOpacity: 0.5,
  },
  pressableBtnTxt:{
    color: '#fff',
    textAlign: 'center',
    fontSize: 20,
    textTransform: 'none'

}

});
