import React, { useState, useEffect, Component } from 'react';
import {StyleSheet, ImageBackground, TextInput, ScrollView, SafeAreaView, View, Text, Image, TouchableHighlight, Pressable, Platform, StatusBar} from 'react-native';

export default function Homescreen({ navigation }){
    return (
              <SafeAreaView style={styles.container}>
        {/* <ImageBackground source={require('../assets/img/bg.jpg')} resizeMode="repeat" style={styles.image}> */}
          <View style={styles.wrapper}>
      <View style={{flex: 2, flexDirection: "row", flexWrap: "wrap"}}>
      <View style={{flex: 1}}><Image source={require('../assets/img/logo1.png')} style={{ width: '60%'}} resizeMode={'center'}></Image></View>
      <View style={{flex: 1}}><Image source={require('../assets/img/logo2.png')} style={{ width: '60%', alignSelf: "flex-end"}} resizeMode={'center'}></Image></View>
      {/* <View><Image source={require('../assets/img/logo2.png')} style={{ width: '30%', alignSelf: 'baseline'}} resizeMode={'center'}></Image></View> */}
      </View>
      <View style={styles.div}>
      <Image source={require('../assets/img/logo3.png')} style={{ width: '95%', alignSelf:'center'}} resizeMode={'center'}></Image>
      </View>
      <View style={styles.div}>
    <Text style={styles.textMiddle}> Welcome </Text>
      </View>
      <View style={styles.div}>
      <Image source={require('../assets/img/logo4.png')} style={{ width: '90%', alignSelf:'center'}} resizeMode={'center'}></Image>
      </View>
      <View style={styles.div}>
        <TouchableHighlight style={styles.pressableBtn} onPress={()=> navigation.navigate('Registration')}>
          <Text style={styles.pressableBtnTxt}>Click Here To Register</Text>
        </TouchableHighlight>
      </View>
      </View>
      {/* </ImageBackground> */}
      </SafeAreaView>

    )
}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fdfbc9',
    paddingTop: Platform.OS === "android" ? StatusBar.currentHeight : 0
  },

  companyName:{
    padding: 5,
    fontSize: 24,
    color: '#2196f3',
  },
  wrapper:{
    flex: 1,
    padding:10,
    overflow: 'scroll'

  },
  textMiddle: {
    textAlign: 'center',
    fontSize: 44,
    color: 'red',
    fontFamily: '',
    fontStyle: 'italic'

  },
  div:{

  },
  div1:{
    flex: 0.5,
    flexDirection: "column",
  },
  div2:{
    flex: 2.5,
    marginTop: 30,
    flexDirection: "column",
    justifyContent: 'space-evenly',
  },
  div3:{
    flex: 3,
    marginTop: 30,
    flexDirection: "column",
    justifyContent: 'space-evenly',

  },
  image:{
    flex: 1, 
    // justifyContent: "center",

  },
  sloganTxt:{
    textAlign: 'center',
    fontSize: 24,
    marginBottom: 10,
    marginTop: 10,
  },
  imgContainer: {
    marginTop: 20,
  },
  BtnText:{
    color:'#fff',
    textAlign:'center',
    fontSize:18,
  },

  pressableBtn:{
    width: '80%',
    padding: 10,
    paddingTop: 13,
    paddingBottom: 13,
    backgroundColor: '#008852',
    alignSelf:'center',
    elevation: 3,
    borderRadius: 5,
    shadowOpacity: 0.5,
  },
  pressableBtnTxt:{
    color: '#fff',
    textAlign: 'center',
    fontSize: 20,
    textTransform: 'none'

}

});
