import React, {useState, useEffect, Component } from 'react';
import {StyleSheet, FlatList, ImageBackground, TextInput, ScrollView, SafeAreaView, View, Text, Alert, Pressable, Platform, StatusBar} from 'react-native';

export default function Homescreen({ navigation }){
  useEffect(()=>{
    loadList()
  }, [])

    const [surname, getSurname] = useState('')
    const [firstname, getFirstname] = useState('')
    const [email, getEmail] = useState('')
    const [phone, getPhone] = useState('')
    const [showForm, setShow] = useState(false)
    const [userData, setUserData] = useState('')

    const lists = [  
        { 
          "id": 0,
        'Surname': 'Ayansiji',
        'Firstname': 'Ayomide',
        'Phone': '+2348162790780',
        'Occupation': 'Engineer',
        'Email':'ayansiji@gmail.com',
      },
      { 
        "id": 1,
        'Surname': 'Bashiru',
        'Firstname': 'Shuaib',
        'Phone': '+2348172790181',
        'Occupation': 'Programmer',
        'Email': 'abiodun@gmail.com',

        },
        { 
          "id": 2,
          'Surname': 'Adegoke',
          'Firstname': 'Emeka',
          'Phone': '+2348692790121',
          'Occupation': 'Developer',
          'Email': 'adegoke@gmail.com',

          }

    ]
    const loadList =()=>{
      setUserData(lists)
    }

    const searchlist = ()=> {

    }

    const verify = (text) =>{
      Alert.alert("Your registration was successful, thanks")
    }

    const signUp = (text) =>{
      Alert.alert("Your registration was successful, thanks")
    }

    const getItemById =(text, id) => {
      Alert.alert(id)
    }

    return (
              <SafeAreaView style={styles.container}>
            <ImageBackground source={require('../img/bg.jpg')} resizeMode="repeat" style={styles.image}>
              <View style={styles.wrapper}>
                <ScrollView>
            {showForm==false? 
                <View style={styles.div3}>
                <View style={styles.wrapText}>
                <Text style={styles.instruction}>Please enter your surname </Text>
                </View>
                <View style={styles.wrapText}>
                <Text style={styles.formLabel}>Surname</Text>
                      <TextInput placeholder='Enter your surname'
                       style={styles.controlField}
                       autoCapitalize= "none"
                       />
                </View>
                <View style={styles.wrapText}>
                <ScrollView style={{flex: 1}}>
                 <FlatList 
                 data={userData}
                 keyExtractor={(item) => item.id}
                 renderItem={({item})=> (

                   <Text onPress={getItemById} style={styles.listItems}>{item.Surname} {item.Firstname}, {item.Email}</Text>
                 )}
                 />
                 </ScrollView>

                  </View>
                <View style={styles.wrapText}>
                  <Pressable style={styles.pressableBtn} onPress={verify}>
                    <Text style={styles.pressableBtnTxt}>Continue</Text>
                  </Pressable>
                  </View>
                </View>
          :
      <View style={styles.div3}>
           <View style={styles.wrapText}>
                <Text style={styles.instruction}>Kindly fill the form below for the event</Text>
            </View>
        <View style={styles.wrapText}>
          <Text style={styles.formLabel}>Surname</Text>
            <TextInput placeholder='Surname'
             style={styles.controlField}
             autoCapitalize= "words"
             />
    </View>
    <View style={styles.wrapText}>
          <Text style={styles.formLabel}>Firstname</Text>
            <TextInput placeholder='Firstname'
             style={styles.controlField}
             autoCapitalize= "words"
             />
    </View>
    <View style={styles.wrapText}>
          <Text style={styles.formLabel}>Email</Text>
            <TextInput placeholder='Email address'
             style={styles.controlField}
             autoCapitalize= "none"
             />
    </View>
    <View style={styles.wrapText}>
          <Text style={styles.formLabel}>Phone number</Text>
            <TextInput placeholder='Phone number'
             style={styles.controlField}
             autoCapitalize= "words"
             />
    </View>
    <View style={styles.wrapText}>
        <Pressable style={styles.pressableBtn} onPress={()=> navigation.navigate('Registration')}>
          <Text style={styles.pressableBtnTxt}>Submit</Text>
        </Pressable>
        </View>
      </View>
      }

{/* Here */}
                </ScrollView>

      </View>
      </ImageBackground>
      </SafeAreaView>

    )
}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    paddingTop: Platform.OS === "android" ? StatusBar.currentHeight : 0
  },

  companyName:{
    fontSize: 24,
  },
  wrapper:{
    flex: 1,
    padding:10,
    overflow: 'scroll'

  },
