  },

  listItems:{
    backgroundColor: '#eee',
    padding: 10,
    paddingTop: 15,
    paddingBottom: 15,
    marginTop: 5,
    fontSize: 20,
  },


  image:{
    flex: 1, 
  },
  wrapText:{
    margin: 15,
  },
  formLabel:{
    fontSize: 22,
    marginBottom: 5,

  },
  controlField:{
    height: 45,
    borderColor: '#ccc',
    borderWidth: 1,
    padding: 7,
    borderRadius: 4,
    fontSize: 22,

  },
  instruction:{
    color: "#2196f3",
    fontSize: 22,
  },
  BtnText:{
    color:'#fff',
    textAlign:'center',
    fontSize:18,
  },

  pressableBtn:{
    width: '100%',
    padding: 10,
    paddingTop: 13,
    paddingBottom: 13,
    backgroundColor:'#2196F3',
    alignSelf:'center',
    elevation: 3,
    borderRadius: 5,
    shadowOpacity: 0.5,
  },
  pressableBtnTxt:{
    color: '#fff',
    textAlign: 'center',
    fontSize: 20,
    textTransform: 'none'

}

});
