import React, {useState, useEffect, Component } from 'react';
import {StyleSheet, ImageBackground, TextInput, SafeAreaView, View, Text, Image, TouchableOpacity, Pressable, Platform, StatusBar} from 'react-native';

export default function Homescreen({ navigation }){

    const [surname, getSurname] = useState('')
    const [firstname, getFirstname] = useState('')
    const [email, getEmail] = useState('')
    const [phone, getPhone] = useState('')


    return (
              <SafeAreaView style={styles.container}>
        <ImageBackground source={require('../img/bg.jpg')} resizeMode="repeat" style={styles.image}>
              <View style={styles.wrapper}>
      <View style={styles.div1}>
        <Text style={styles.companyName}>VirtualStack</Text>
      </View>
      <View style={styles.div3}>
      <View style={styles.wrapText}>
      <Text style={styles.instruction}>Please start by verifying your email address </Text>
      </View>
      <View style={styles.wrapText}>
      <Text style={styles.formLabel}>Email </Text>
            <TextInput placeholder='Your email'
             style={styles.controlField}
             autoCapitalize= "none"
             />
      </View>
      <View style={styles.wrapText}>
        <Pressable style={styles.pressableBtn} onPress={()=> navigation.navigate('Registration')}>
          <Text style={styles.pressableBtnTxt}>Verify</Text>
        </Pressable>
        </View>
      </View>
      <View style={styles.div3}>
        <View style={styles.wrapText}>
          <Text style={styles.formLabel}>Surname</Text>
            <TextInput placeholder='Surname'
             style={styles.controlField}
             autoCapitalize= "words"
             />
    </View>
    <View style={styles.wrapText}>
          <Text style={styles.formLabel}>Firstname</Text>
            <TextInput placeholder='Firstname'
             style={styles.controlField}
             autoCapitalize= "words"
             />
    </View>
    <View style={styles.wrapText}>
          <Text style={styles.formLabel}>Email</Text>
            <TextInput placeholder='Email address'
             style={styles.controlField}
             autoCapitalize= "none"
             />
    </View>
    <View style={styles.wrapText}>
          <Text style={styles.formLabel}>Phone number</Text>
            <TextInput placeholder='Phone number'
             style={styles.controlField}
             autoCapitalize= "words"
             />
    </View>
    <View style={styles.wrapText}>
        <Pressable style={styles.pressableBtn} onPress={()=> navigation.navigate('Registration')}>
          <Text style={styles.pressableBtnTxt}>Submit</Text>
        </Pressable>
        </View>
      </View>
      </View>
      </ImageBackground>
      </SafeAreaView>

    )
}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    paddingTop: Platform.OS === "android" ? StatusBar.currentHeight : 0
  },

  companyName:{
    fontSize: 24,
  },
  wrapper:{
    flex: 1,
    padding:10,
    overflow: 'scroll'

  },
  div1:{
    flex: 0.5,
    flexDirection: "column",
  },

  div3:{
    flex: 5,
    marginTop: 10,
    flexDirection: "column",

  },
  image:{
    flex: 1, 
  },
  wrapText:{
    margin: 15,
  },
  formLabel:{
    fontSize: 22,
    marginBottom: 5,

  },
  controlField:{
    height: 45,
    borderColor: '#ccc',
    borderWidth: 1,
    padding: 7,
    borderRadius: 4,
    fontSize: 22,

  },
  instruction:{
    color: "#ccc"
  },
  BtnText:{
    color:'#fff',
    textAlign:'center',
    fontSize:18,
  },

  pressableBtn:{
    width: '100%',
    padding: 10,
    paddingTop: 13,
    paddingBottom: 13,
    backgroundColor:'#2196F3',
    alignSelf:'center',
    elevation: 3,
    borderRadius: 5,
    shadowOpacity: 0.5,
  },
  pressableBtnTxt:{
    color: '#fff',
    textAlign: 'center',
    fontSize: 20,
    textTransform: 'none'

}

});
