import React, { Component } from 'react';
import {StyleSheet, ImageBackground, TextInput, SafeAreaView, View, Text, Image, Pressable} from 'react-native';
  export default function Homescreen({ navigation }){
    return (
      <View style={styles.container}>
        <ImageBackground source={require('../img/bg.jpg')} resizeMode="repeat" style={styles.image}>
      <View style={styles.wrapper}>
        <Text style={styles.sloganTxt}>Artwallet, Digital Solutions for everyone</Text>

        <Pressable style={styles.pressableBtn}>
          <Text style={styles.pressableBtnTxt}>Register for the event</Text>
        </Pressable>
      </View>
      </ImageBackground>

      </View>
    )
}


const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  wrapper:{
    flex: 1,
    padding:10,

  },
  image:{
    flex: 1, 
    justifyContent: "center",

  },
  sloganTxt:{
    textAlign: 'center',
    fontSize: 24,
    marginBottom: 10,
    marginTop: 10,
  },
  imgContainer: {
    marginTop: 20,
  },
  BtnText:{
    color:'#fff',
    textAlign:'center',
    fontSize:18,
  },

  pressableBtn:{
    width: '80%',
    padding: 10,
    paddingTop: 13,
    paddingBottom: 13,
    backgroundColor:'#2196F3',
    alignSelf:'center',
    elevation: 3,
    borderRadius: 5,
    shadowOpacity: 0.5,
  },
  pressableBtnTxt:{
    color: '#fff',
    textAlign: 'center',
    fontSize: 20,
    textTransform: 'uppercase'

}

});
