import React, {useState, useEffect, Component } from 'react';
import {StyleSheet, FlatList, LisHeaderComponent, TouchableHighlight, ListFooterComponent, Image, ImageBackground, TouchableWithoutFeedback, TextInput, ScrollView, SafeAreaView, View, Text, Alert, Pressable, Platform, StatusBar} from 'react-native';

export default function SuccessPageScreen({navigation, route}){
  useEffect(()=>{
    addRecord()
  }, [])
  const [progress, setProgress] = useState('')
  const [success, setSuccess] = useState('')
  const [message, setMessage] = useState('')

  const addRecord =()=> {
    setProgress('Please wait while submitting your details...')
    const form = new FormData()
    form.append('surname', navigation.getParam('surname'))
    form.append('firstname', navigation.getParam('email'))
    form.append('email', navigation.getParam('email'))
    form.append('phone', navigation.getParam('phone'))
    form.append('speciality', navigation.getParam('speciality'))
    form.append('place_of_practice', navigation.getParam('place_of_practice'))
    axios.get('https://events.latechnologiesltd.com/events/account', form)
    .then(function(response){
      Alert.alert("")

    }).catch(error =>{
      recordCheck(false)
      alertCheck('Error connecting! check your network connection or try again')
    }) 
  }


    return (
              <View style={styles.container}>
                <View style={styles.div1}>
              <Image source={require('../assets/img/logo4.png')} style={{ width: '95%', height:150, alignSelf:'center'}} resizeMode={'center'}></Image>
              </View>
                <View style={styles.div3}>

                             <Text style={styles.progress}>{progress}</Text>
                             <Text style={styles.textMiddleHeader}>Thanks, {navigation.getParam('surname')}!</Text>
                             <Text style={styles.textMiddle}>We have received your information provided!</Text>
                </View>

                <View style={styles.div4}>
        <TouchableHighlight style={styles.pressableBtn} onPress={()=> navigation.navigate('Home')}>
          <Text style={styles.pressableBtnTxt}>Close this page</Text>
        </TouchableHighlight>
      </View>
      </View>

    )
}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'column',
    backgroundColor: '#fdfbc9',
    paddingTop: Platform.OS === "android" ? StatusBar.currentHeight : 0
  },


  wrapper:{
    flex: 1,
    flexDirection: 'column',
    alignItems: 'stretch',
    padding:10,
  },
  progress:{
    textAlign: 'center',
    fontSize: 26,
    marginBottom: 10,
    fontWeight: 'bold',
    color: '#008852',
    textTransform: 'capitalize'
  },
  textMiddleHeader: {
    textAlign: 'center',
    fontSize: 30,
    marginBottom: 10,
    fontWeight: 'bold',
    color: '#008852',
    textTransform: 'capitalize'

  },
  textMiddle: {
    textAlign: 'center',
    fontSize: 25,
    color: '#008852',

  },

  div1:{
    flex: 2,
    flexDirection: "column",
    alignItems: 'center',
  },

  div3:{
    flex: 3,
